import React, { useState } from "react";

import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBContainer,
  MDBIcon,
  MDBRow,
  MDBCard,
  MDBCardBody,
  MDBCol,
  MDBTabContent,
  MDBTabPane
} from "mdbreact";

import { useSelector, useDispatch } from "react-redux";

import TopNavBar from "../TopNavigationBar";

const Dashboard = props => {
  const [state, setstate] = useState({ activePill: "1" });

  const togglePills = tab => {
    if (state.activePill !== tab) {
      setstate({ activePill: tab });
    }
  };

  const changeTab = e => {
    e.preventDefault();

    setstate({ activePill: (+state.activePill + 1).toString() });
  };

  return (
    <MDBContainer>
      <TopNavBar />
      <MDBRow className="my-2" center>
        <MDBCard className="w-100">
          <MDBCardBody>
            <MDBRow>
              <MDBCol lg="12" className="mb-4">
                <MDBNav pills color="elegant" className="nav-justified">
                  <MDBNavItem>
                    <MDBNavLink
                      to="#"
                      className={state.activePill === "1" ? "active" : ""}
                      onClick={() => togglePills("1")}
                    >
                      {state.activePill === "1" ? (
                        <strong>
                          <MDBIcon icon="angle-double-right" />
                          &nbsp;&nbsp; 1. Billing
                        </strong>
                      ) : (
                        <strong style={{ color: "#59698d !important" }}>
                          1. Billing
                        </strong>
                      )}
                    </MDBNavLink>
                  </MDBNavItem>

                  <MDBNavItem>
                    <div style={{ backgroundColor: "#f5f5f5 !important" }}>
                      <MDBNavLink
                        to="#"
                        className={state.activePill === "2" ? "active" : ""}
                        onClick={() => togglePills("2")}
                      >
                        {state.activePill === "2" ? (
                          <strong>
                            <MDBIcon icon="angle-double-right" />
                            &nbsp;&nbsp;2. Shipping
                          </strong>
                        ) : (
                          <strong>2. Shipping</strong>
                        )}
                      </MDBNavLink>
                    </div>
                  </MDBNavItem>

                  <MDBNavItem>
                    <MDBNavLink
                      to="#"
                      className={state.activePill === "3" ? "active" : ""}
                      onClick={() => togglePills("3")}
                    >
                      {state.activePill === "3" ? (
                        <strong>
                          <MDBIcon icon="angle-double-right" />
                          &nbsp;&nbsp; 3. Payment
                        </strong>
                      ) : (
                        <strong>3. Payment</strong>
                      )}
                    </MDBNavLink>
                  </MDBNavItem>
                </MDBNav>
                <MDBTabContent className="pt-4" activeItem={state.activePill}>
                  <MDBTabPane tabId="1">
                    <h4 className="mb-4 mt-1 h5 text-center font-weight-bold">
                      tab1
                    </h4>
                  </MDBTabPane>
                  <MDBTabPane tabId="2">
                    <h4 className="mb-4 mt-1 h5 text-center font-weight-bold">
                      tab2
                    </h4>
                  </MDBTabPane>
                  <MDBTabPane tabId="3">
                    <h4 className="mb-4 mt-1 h5 text-center font-weight-bold">
                      tab3
                    </h4>
                  </MDBTabPane>
                </MDBTabContent>
              </MDBCol>
            </MDBRow>
          </MDBCardBody>
        </MDBCard>
      </MDBRow>
    </MDBContainer>
  );
};
export default Dashboard;
