import React, { useState } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput,
  MDBBtn,
  MDBIcon,
  MDBCard,
  MDBCardBody,
  MDBModal,
  MDBModalBody,
  MDBModalFooter,
  MDBEdgeHeader,
  MDBFreeBird
} from "mdbreact";
import { useSelector, useDispatch } from "react-redux";

import TopNav from "../TopNavigationBar";
import SectionContainer from "../sectionContainer";
import { loginUser } from "../../store/users/actions";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const loggedInUser = useSelector(state => state.users.loggedInUser);

  const [loginStatus, setLoginStatus] = useState("");

  const dispatch = useDispatch();

  const loginSubmit = e => {
    e.preventDefault();
    let user = {
      username: username,
      password: password
    };

    // dispatch(functionname(loginUser))
    dispatch(loginUser(user));

    // console.log("SUBMIT!");
    // console.log("Username: ", username, " Password: ", password);
    console.log("LoggedInUser AFTER DISPATCH: ", loggedInUser);
  };

  console.log("LoggedInUser: ", loggedInUser);

  const loginResult = returnedUser => {
    if (returnedUser.id === 0) {
      setLoginStatus("Your username or password do not match.");
    } else setLoginStatus("Login Successful!");
  };

  return (
    <div>
      <TopNav />

      {/* <div style={{ marginTop: "30px" }} /> */}
      <div className="text-left">
        <MDBContainer className="mt-3">
          {/* <MDBEdgeHeader color="mdb-color darken-2"></MDBEdgeHeader> */}
          <MDBEdgeHeader color="elegant-color-dark"></MDBEdgeHeader>

          <MDBFreeBird>
            <SectionContainer header="" className="row" noBorder>
              <MDBCol md="3" />
              <MDBCol md="6">
                <MDBCard>
                  <MDBCardBody>
                    <form onSubmit={loginSubmit}>
                      <p className="h4 text-center py-4">Login</p>
                      {loggedInUser.firstName === ""
                        ? ""
                        : "You logged in successfully."}
                      <div className="grey-text">
                        <MDBInput
                          label="Username"
                          icon="user-alt"
                          group
                          type="text"
                          validate
                          error="wrong"
                          success="right"
                          onChange={e => setUsername(e.target.value)}
                        />
                        <MDBInput
                          label="Password"
                          icon="lock"
                          group
                          type="password"
                          validate
                          onChange={e => setPassword(e.target.value)}
                        />
                      </div>
                      <div className="text-center py-4 mt-3">
                        <MDBBtn type="submit" color="dark">
                          Login
                        </MDBBtn>
                      </div>
                    </form>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
              <MDBCol md="3" />
            </SectionContainer>
          </MDBFreeBird>
        </MDBContainer>
      </div>
    </div>
  );
};
export default Login;
