import React, { useState } from "react";
import { MDBRow, MDBCol } from "mdbreact";

const EditItem = props => {
  const [title, setTitle] = useState(props.item.title);
  const [summary, setSummary] = useState(props.item.summary);
  const [price, setPrice] = useState(props.item.price);
  const [description1, setDescription1] = useState(props.item.description1);
  const [description2, setDescription2] = useState(props.item.description2);

  //   if (props.currentItem.id === 0) {
  // setTitle("You have no item selected")
  //   } else {
  // setTitle(props.currentItem.title);
  // setSummary(props.currentItem.summary);
  // setPrice(props.currentItem.price);
  // setDescription1(props.currentItem.description1);
  // setDescription2(props.currentItem.setDescription2);
  //   }

  return (
    <form>
      <MDBRow>
        <MDBCol md="6" className="mb-4" style={{ textAlign: "left" }}>
          {props.item.title === "No item selected." ? (
            <div style={{ textAlign: "center" }}>
              <h5
                className="mb-4 mt-1 h5 text-center font-weight-bold"
                style={{ textAlign: "center" }}
              >
                No Item Selected.
              </h5>
            </div>
          ) : (
            <div>
              {" "}
              <label htmlFor="title">Name</label>
              <input
                type="text"
                id="title"
                className="form-control"
                defaultValue={title}
              />
            </div>
          )}
        </MDBCol>
      </MDBRow>
    </form>
  );
};
export default EditItem;
