import React, { useState } from "react";
import { MDBRow, MDBCol, MDBBtn } from "mdbreact";

const EditItem = props => {
  const [title, setTitle] = useState(props.item.title);
  const [summary, setSummary] = useState(props.item.summary);
  const [price, setPrice] = useState(props.item.price);
  const [description1, setDescription1] = useState(props.item.description1);
  const [description2, setDescription2] = useState(props.item.description2);

  //   if (props.currentItem.id === 0) {
  // setTitle("You have no item selected")
  //   } else {
  // setTitle(props.currentItem.title);
  // setSummary(props.currentItem.summary);
  // setPrice(props.currentItem.price);
  // setDescription1(props.currentItem.description1);
  // setDescription2(props.currentItem.setDescription2);
  //   }

  const editClicked = () => {
    console.log("title: ", title);
    console.log("summary", summary);
    console.log("price", price);
    console.log("description1", description1);
    console.log("description2", description2);
  };

  return (
    <form>
      <MDBRow>
        <MDBCol className="mb-4">
          {props.item.title === "No item selected." ? (
            // <div
            //   style={{ display: "block", width: "100%", textAlign: "center" }}
            // >
            //   <h5
            //     className="mb-4 mt-1 h5 text-center font-weight-bold"
            //     style={{ textAlign: "center" }}
            //   >
            //     No Item Selected.
            //   </h5>
            // </div>
            <h4 className="mb-4 mt-1 h5 text-center font-weight-bold">
              No Item Selected.
            </h4>
          ) : (
            <div style={{ textAlign: "left" }}>
              <label htmlFor="title">Name</label>
              <input
                type="text"
                id="title"
                className="form-control"
                defaultValue={title}
                onChange={e => setTitle(e.target.value)}
              />
              <hr />
              <label htmlFor="title">Price</label>
              <input
                type="number"
                id="price"
                className="form-control"
                defaultValue={price}
                step=".01"
                onChange={e => setPrice(e.target.value)}
              />
              <hr />
              <label htmlFor="title">Summary</label>
              <textarea
                type="text"
                id="summary"
                className="form-control"
                defaultValue={summary}
                onChange={e => setSummary(e.target.value)}
              />
              <hr />
              <label htmlFor="title">Description 1</label>
              <textarea
                type="text"
                id="description1"
                className="form-control"
                defaultValue={description1}
                onChange={e => setDescription1(e.target.value)}
              />
              <hr />
              <label htmlFor="title">Description 2</label>
              <textarea
                type="text"
                id="description2"
                className="form-control"
                defaultValue={description2}
                onChange={e => setDescription2(e.target.value)}
              />
              <hr />
              <MDBBtn
                color="elegant"
                size="lg"
                block
                onClick={props.saveChangesClick({
                  title: title,
                  summary: summary,
                  price: price,
                  description1: description1,
                  description2: description2
                })}
                // type="submit"
              >
                <b>Save Changes</b>
              </MDBBtn>
            </div>
          )}
        </MDBCol>
      </MDBRow>
    </form>
  );
};
export default EditItem;
