import React, { useState } from "react";
import { BrowserRouter, Redirect, Router } from "react-router-dom";
// import { withRouter } from "react-router";
import {
  MDBNav,
  MDBNavItem,
  MDBNavLink,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBContainer,
  MDBEdgeHeader,
  MDBFreeBird,
  MDBIcon,
  MDBNavbarBrand
} from "mdbreact";
import { useSelector } from "react-redux";
import { Link, withRouter } from "react-router-dom";

// import logo from "../../public/H2Studio-small.png";

const TopNavigationBar = props => {
  const users = useSelector(state => state.users.all);

  const [pageRedirect, setPageRedirect] = useState("");

  console.log("USERS: ", users);

  const onStoreClick = () => {
    setPageRedirect("store");
    console.log("CLICKED");
    props.history.push("/store");
  };

  if (pageRedirect === "store") {
    setPageRedirect("");
    console.log("GOT THIS FAR");
    return <Redirect push to="/store" />;
  } else if (pageRedirect === "custom") {
    setPageRedirect("");
    return <Redirect push to="/custom" />;
  } else if (pageRedirect === "business") {
    setPageRedirect("");
    return <Redirect push to="/business" />;
  }

  return (
    <MDBContainer>
      <MDBNav
        left
        pills
        color="dark"
        style={{ marginTop: "15px", backgroundColor: "white" }}
      >
        <img
          src="https://i.ibb.co/dQcy0LK/H2-STUDIO-FONT-ON-SIDE-BLACK.png"
          alt="logo"
          height="60"
          style={{ marginLeft: "15px", marginRight: "15px" }}
        />
        <MDBNavItem style={{ paddingTop: "15px", paddingBottom: "5px" }}>
          <MDBNavLink to={{ pathname: "/home" }}>Home</MDBNavLink>
        </MDBNavItem>

        <MDBNavItem style={{ paddingTop: "15px", paddingBottom: "5px" }}>
          <MDBNavLink to={{ pathname: "/about" }}>About Us</MDBNavLink>
        </MDBNavItem>

        <MDBNavItem style={{ paddingTop: "15px", paddingBottom: "5px" }}>
          <MDBDropdown>
            <MDBDropdownToggle nav caret color="dark">
              Products
            </MDBDropdownToggle>
            <MDBDropdownMenu color="dark">
              <MDBDropdownItem onClick={onStoreClick}>Store</MDBDropdownItem>
              <MDBDropdownItem onClick={onStoreClick}>Custom</MDBDropdownItem>
              <MDBDropdownItem>Business</MDBDropdownItem>
              {/* <MDBDropdownItem divider />
              <MDBDropdownItem>Separated link</MDBDropdownItem> */}
            </MDBDropdownMenu>
          </MDBDropdown>
        </MDBNavItem>

        <MDBNavItem
          style={{
            marginLeft: "auto",
            paddingTop: "15px",
            paddingBottom: "5px"
          }}
        >
          <MDBNavLink to="/cart">
            <MDBIcon icon="shopping-cart" />
            &nbsp;&nbsp;Cart
          </MDBNavLink>
        </MDBNavItem>
        <MDBNavItem style={{ paddingTop: "15px", paddingBottom: "5px" }}>
          <MDBNavLink
            to={{
              pathname: "/login",
              state: {}
            }}
          >
            Login
          </MDBNavLink>
          {/* <Link
            to={{
              pathname: "/login",
              state: {}
            }}
          >
            Login
          </Link> */}
          {/* </MDBNavLink> */}
        </MDBNavItem>
      </MDBNav>
    </MDBContainer>
  );
};

export default withRouter(TopNavigationBar);
