import React, { useState } from "react";
import TopNavigationaBar from "../TopNavigationBar";
import { useSelector, useDispatch } from "react-redux";
import CartItem from "./CartItem";

import {
  MDBRow,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBTooltip,
  MDBTable,
  MDBTableBody,
  MDBTableHead,
  MDBInput,
  MDBBtn,
  MDBIcon
} from "mdbreact";

import { updateCart } from "../../store/cart/actions";

const Cart = props => {
  const [columns, setcolumns] = useState({});
  const [update, setUpdate] = useState("");
  const cart = useSelector(state => state.shoppingCart.cart);
  //const products = useSelector(state => state.products.all);
  const dispatch = useDispatch();
  const [newCart, setNewCart] = useState(cart);

  //   const newCart = cart;

  const removeOneFromCart = id => {
    let itemIndex = newCart.findIndex(cartItem => cartItem.id === id);
    console.log("itemIndex: ", itemIndex);

    //   let updatedCart =
    console.log("cart before: ", cart);
    // setNewCart(cart);
    // console.log("new cart before findindex, after setting again: ", newCart);

    // console.log("NEWCART BEFORE: ", newCart);

    // let updatedCart = cart.filter((item, index) => {
    //   console.log("index: ", index, " itemIndex: ", itemIndex);
    //   if (index === itemIndex) {
    //     console.log("MATCH!");
    //   } else {
    //     return item;
    //   }
    // });

    let updatedCart = cart.filter((item, index) => {
      return index !== itemIndex;
    });

    console.log("updated cart: ");

    setNewCart(updatedCart);
    console.log("NEWCART AFTER: ", newCart);

    // console.log("new cart before: ", newCart);
    // setNewCart(newCart.splice(parseInt(itemIndex), 1));
    // console.log("new cart after: ", newCart);

    // let updatedCart = cart.splice(itemIndex, 1);
    // console.log("updated cart: ", updatedCart);
    // setNewCart(updatedCart);
    dispatch(updateCart(newCart));
    setUpdate(id);
  };

  console.log("CART CONTENTS: ", cart);

  //Count number of items in cart to determine quantities
  let cartCount = {};
  cart.forEach(cartItem => {
    if (cartCount.hasOwnProperty(cartItem.id)) {
      cartCount[cartItem.id]++;
    } else {
      cartCount[cartItem.id] = 1;
    }
  });

  //Create an array matching cart contents with quantities
  let cartArrayWithQuantities = [];
  for (const key in cartCount) {
    let currentCartItem = cart.find(cartItem => cartItem.id == key);
    currentCartItem["quantity"] = cartCount[key];
    cartArrayWithQuantities.push(currentCartItem);
  }
  console.log("cartArrayWithQuantities: ", cartArrayWithQuantities);

  let cartRows = cartArrayWithQuantities.map(cartItem => (
    <CartItem
      product={cartItem}
      key={cartItem.id}
      removeOneFromCart={removeOneFromCart}
    />
  ));

  return (
    <div>
      <TopNavigationaBar />
      <MDBRow className="my-2" center>
        <MDBCard className="w-100" style={{ marginTop: "15px" }}>
          {cartRows.length === 0 ? (
            <MDBCardBody>
              <MDBCardTitle style={{ margin: "auto" }}>
                <MDBIcon icon="shopping-cart"></MDBIcon>&nbsp;&nbsp;You have
                nothing in your cart.
              </MDBCardTitle>
            </MDBCardBody>
          ) : (
            <MDBCardBody>
              <MDBTable hover className="product-table">
                <MDBTableHead
                  className="font-weight-bold"
                  //color="mdb-color lighten-5"
                  color="elegant-color-dark"
                  textWhite
                >
                  <tr>
                    <th></th>
                    <th>Product</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Amount</th>
                    <th></th>
                  </tr>
                </MDBTableHead>

                <MDBTableBody>{cartRows}</MDBTableBody>
              </MDBTable>
            </MDBCardBody>
          )}
        </MDBCard>
      </MDBRow>
    </div>
  );
};
export default Cart;
